mapper.select:
select
r.id,r.user_id,r.name,r.organ_id,r.organ,r.start_date,r.reason,r.day,r.type_id,t.name type_name,r.insert_time
from jk_leave_record r
left join jk_work_type t on r.type_id = t.id where 1=1

    and r.name like concat('%',#{name},'%')


    and r.organ_id = #{organId}


    and r.start_date = #{date}


    and r.day = #{day}


    and r.type_id = #{typeId}

select
r.id,r.user_id,r.name,r.organ_id,r.organ,r.start_date,r.reason,r.day,r.type_id,t.name type_name,t.pid,r.insert_time
from jk_leave_record r
left join jk_work_type t on r.type_id = t.id
where 1=1

    and r.start_date = #{date}



    and r.organ_id = #{organId}


    and r.organ_id in

        #{item.id}
